import React, {createContext, useState} from 'react'

export const CurrentUserContext = createContext([{}, () => {}])

export const CurrentUserProvider = ({children}) => {//то во что мы обернем компоненты для оступа к контексту
  const [state, setState] = useState({ // делаем useState, дл пеедачи наружу
    isLoading: false,
    isLoggedIn: null,
    currentUser: null
  })

  return (
  <CurrentUserContext.Provider value={[state, setState]}> {/*предостав-т значение для этого контекста(передаем наружу)*/}
        {children}
    </CurrentUserContext.Provider>
  )
}
